import React, { Component } from 'react';
import MainHeading from "../../ui/MainHeading/MainHeading";
import styled, { keyframes } from "styled-components";
import FadeIn from "react-animations/lib/fade-in";

const FadeInAnimation = keyframes`${FadeIn}`;
const FadeInDiv = styled.div`animation: 5s ${FadeInAnimation};`;

class Tv extends Component {
  constructor() {
    super();
    this.state = {
      items: []
    }; 
  }

  componentDidMount() {
    const proxyurl = "https://cors-anywhere.herokuapp.com/";
    const url = "https://rss.itunes.apple.com/api/v1/us/tv-shows/top-tv-episodes/all/10/explicit.json";

    fetch(proxyurl + url)
      .then(res => res.json())
      .then(res => {
        this.setState({
          items: res.feed.results
        });
      });
  }


  render() {
    var { items } = this.state;
    var itemInfo = items.map(item => 
      <div className="col-sm-12">
        <div className="container__tv">
          <div className="tv__img">
              <img src={item.artworkUrl100} alt={item.artworkUrl100} />
          </div>
          <div className="tv__title">
              Tytuł: {item.artistName}
          </div>
          <div className="tv__release">
              Data premiery: {item.releaseDate}
          </div>
          <div className="tv_link">
            <a href={item.url}>Więcej</a>
          </div>
        </div>
      </div>);
      console.log(items);
    return (
      <div className="col-sm-12 col-md-6 tv">
        <MainHeading>
          Programy telewizyjne
        </MainHeading>
        <FadeInDiv className="container">
          <div className="row">
            {itemInfo}
          </div>
        </FadeInDiv>
      </div>
    );
  }
}

export default Tv;
